import React from 'react';
import {useState} from 'react';
import './Search.scss';

const fetchArtist = async inputArtist => {
    console.log(inputArtist);
}

const Search = () => {
    const [inputArtist, setInputArtist]= useState('');

    const updateArtist = e => {
        setInputArtist(e.target.value);
    }

    return (
        <div className='Search'>
            <div className='Search__icon-container'>
                <div className="Search__icon"></div>
            </div>
            <input type="text" onKeyUp={updateArtist} onClick={fetchArtist(inputArtist)} className="Search__input" id="search-bar" placeholder="Type here to search" />
        </div>
    );
};

export default Search;
