import React from 'react';
import {useState} from 'react';

import './Search.scss';

import { spotifyApi } from "../../backend/spotifyAuth";

const Search = () => {
    const [inputArtist, setInputArtist]= useState('');
    const inputButton = document.getElementById('search-bar-btn');

    const updateArtist = e => {
        e.keyCode === 13 ? inputButton.click() : setInputArtist(e.target.value);

        e.preventDefault();
    }

    const fetchArtist = async () => {
        spotifyApi.clientCredentialsGrant().then(
            function(data) {
                console.log('The access token expires in ' + data.body['expires_in']);
                console.log('The access token is ' + data.body['access_token']);

                // Save the access token so that it's used in future calls
                return spotifyApi.getArtist(inputArtist)
                    .then(function(data) {
                        console.log('Artists information', data.body);
                    }, function(err) {
                        console.error(err);
                    });
            },
            function(err) {
                console.log(
                    'Something went wrong when retrieving an access token',
                    err.message
                );
            }
        );
    }

    return (
        <div className='Search'>
            <div className='Search__icon-container'>
                <div className="Search__icon"></div>
            </div>
            <input type="text" onKeyUp={updateArtist} className="Search__input" id="search-bar" placeholder="Type here to search" />
            <button onClick={fetchArtist} className="Search__btn" id="search-bar-btn"></button>
        </div>
    );
};

export default Search;
